import React, {Component} from "react";

const conteudos = [
    {
      conteudo: 'High Order Functions',
      bloco: 8,
      status: 'Aprendido'
    },
    {
      conteudo: 'Composicao de Componentes',
      bloco: 11,
      status: 'Aprendendo',
    },
    {
      conteudo: 'Composicao de Estados',
      bloco: 12,
      status: 'Aprenderei'
    },
    {
      conteudo: 'Redux',
      bloco: 16,
      status: 'Aprenderei'
    },
  ];


const task = (value) => {

    return (
        value.map((elemento) => {
       return `Bloco::${ elemento.bloco } 

       Conteudo:${elemento.conteudo}`
            }  )
    );
  }

class Content extends Component{
    render(){
        return task(conteudos);
    }
}


export default Content
